import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import Contato from '../../models/Contato'

const contatosSlice = createSlice({
  name: 'tarefas',
  initialState: {
    itens: [
        new Contato('Gilberto Garcia', 'gilberto@gmail.com', '(69) 99950-4002', 1),
        new Contato('Felipe Lira', 'gilberto@gmail.com', '(69) 99950-4002', 2),
        new Contato('Tanous Melo', 'gilberto@gmail.com', '(69) 99950-4002', 3),
        new Contato('Reinoldo Garcia', 'gilberto@gmail.com', '(69) 99950-4002', 4)
    ]
  },
  reducers: {
    remover: (state, action: PayloadAction<number>) => {
      state = state.filter((contato) => contato.id !== action.payload)
    }
  }
})

export const { remover } = contatosSlice.actions

export default contatosSlice.reducer
