import styled from 'styled-components'

export const Card = styled.div`
  background-color: #fcfcfc;
  padding: 28px;
  margin-bottom: 32px;
  width: 250px;
  border-radius: 16px;
`

export const NomeContato = styled.h2`
  font-size: 18px;
  font-weight: bold;
`

export const TituloCampo = styled.label`
  font-size: 14px;
  font-weight: bold;
`

export const Campo = styled.input`
  color: #8b8b8b;
  font-size: 14px;
  display: block;
  width: 100%;
`

export const BarraAcoes = styled.div`
  border-top: 1px solid rgba(0, 0, 0, 0.1);
  padding-top: 16px;
`

export const Botao = styled.button`
  font-weight: bold;
  font-size: 12px;
  color: #fff;
  padding: 8px 12px;
  border: none;
  cursor: pointer;
  background-color: #111;
  border-radius: 8px;
  margin-right: 8px;
`

const BotaoSalvar = styled(Botao)
