import { FormEvent, useState } from 'react'
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'

import { FormContainer, HeaderAddContato, Titulo } from './styles'
import { cadastrar } from '../../store/reducers/contatos'
import { BotaoEditar } from '../../components/Contato/styles'

const Formulario = () => {
  const dispatch = useDispatch()
  const navigate = useNavigate()

  const [nome, setNome] = useState('')
  const [email, setEmail] = useState('')
  const [telefone, setTelefone] = useState('')

  const cadastrarContato = (e: FormEvent) => {
    e.preventDefault()

    dispatch(
      cadastrar({
        nome,
        email,
        telefone
      })
    )
    navigate('/')
  }

  return (
    <>
      <HeaderAddContato>
        <button onClick={() => navigate('/')}>‚Üê</button>
        <Titulo>Novo Contato</Titulo>
      </HeaderAddContato>
      <FormContainer onSubmit={cadastrarContato}>
        <label htmlFor="nome">Nome</label>
        <input
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          id="nome"
          type="text"
          placeholder="Nome"
        />
        <label htmlFor="email">E-mail</label>
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          id="email"
          type="text"
          placeholder="E-mail"
        />
        <label htmlFor="telefone">Telefone</label>
        <input
          value={telefone}
          onChange={(e) => setTelefone(e.target.value)}
          id="telefone"
          type="text"
          placeholder="Telefone"
        />
        <BotaoEditar type="submit">Cadastrar</BotaoEditar>
      </FormContainer>
    </>
  )
}

export default Formulario
